import 'package:flutter/material.dart';

class StatefullFlag extends StatefulWidget {
  const StatefullFlag({super.key});

  @override
  State<StatefullFlag> createState() => _StatefullFlagState();
}

class _StatefullFlagState extends State<StatefullFlag> {
  int _couter = 0;
  Widget returnStick() {
    if (_couter >= 1) {
      return Container(
        alignment: Alignment.topLeft,
        height: 550,
        width: 20,
        color: Colors.black,
      );
    } else {
      return const SizedBox(height: 0);
    }
  }

  Widget returnOrange() {
    if (_couter >= 2) {
      return Container(
        width: 250,
        height: 70,
        color: Colors.orange,
      );
    } else {
      return const SizedBox(width: 0);
    }
  }

  Widget returnWhite() {
    if (_couter >= 3) {
      return Container(
        width: 250,
        height: 70,
        color: Colors.white,
        child: (_couter >= 1) ? returnImage() : Container(),
      );
    } else {
      return const SizedBox(width: 0);
    }
  }

  Widget returnImage() {
    if (_couter >= 4) {
      return Image.network(
          "data:image/jpeg;base64,");
    } else {
      return const SizedBox(width: 0);
    }
  }

  Widget returnGreen() {
    if (_couter >= 5) {
      return Container(
        width: 250,
        height: 70,
        color: Colors.green,
      );
    } else {
      return const SizedBox(width: 0);
    }
  }

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      backgroundColor: Colors.grey,
      appBar: AppBar(title: const Text("India Flag")),
      floatingActionButton: FloatingActionButton(
        onPressed: () {
          setState(() {
            // print("hiiiii $_couter");
            _couter++;
          });
        },
        child: const Text("add"),
      ),
      body: Center(
        child: Column(
          mainAxisAlignment: MainAxisAlignment.center,
          children: [
            Row(
              mainAxisAlignment: MainAxisAlignment.center,
              crossAxisAlignment: CrossAxisAlignment.start,
              children: [
                returnStick(),
                Column(
                  children: [
                    returnOrange(),
                    returnWhite(),
                    returnGreen(),
                  ],
                )
              ],
            ),
          ],
        ),
      ),
    );
  }
}
